const pinyin=require("pinyin"),CHINESE=/[\u4e00-\u9fa5]/;function addPinyin(str){return CHINESE.test(str)?str+" "+pinyin(str,{style:pinyin.STYLE_NORMAL}).join(""):str}module.exports=({relativeToRoot:relativeToRoot,config:config})=>{return`\n    <!doctype html>\n    <html lang="en">\n      <head>\n        <meta charset="utf-8">\n        <meta http-equiv="x-ua-compatible" content="ie=edge">\n        <meta name="viewport" content="width=device-width, initial-scale=1.0,\n          maximum-scale=1.0, user-scalable=no"/>\n        \n        <title>${config.title}</title>\n\n        <link rel="shortcut icon" type="image/png" href="/favicon.ico"/>\n        <link rel="stylesheet" href="${relativeToRoot}/css/mobi.css/mobi.min.css" />\n        <link rel="stylesheet" href="${relativeToRoot}/css/site.css" />\n      </head>\n      <body>\n        <div class="flex-center">\n          <div class="container">\n            ${renderSearch()}\n            ${renderBookmarks()}\n            ${renderFooter()}\n          </div>\n        </div>\n\n        <script src="${relativeToRoot}/js/search.js"><\/script>\n      </body>\n    </html>\n  `;function renderSearch(){return'\n      <style id="search-style"></style>\n      <form id="search-form" class="form">\n        <input id="search-input" type="search" placeholder="Type to search" autocomplete="off" tabindex="1" />\n      </form>\n    '}function renderBookmarks(){return`\n      <ul class="site-bookmark-ul flex-left flex-wrap units-gap">\n        ${config.bookmarks.map(renderBookmark).join("")}\n      </ul>\n    `}function renderBookmark({category:category,name:name,url:url,favicon:favicon}){return category?`\n        <li class="site-bookmark-li site-bookmark-category unit-1 top-gap text-muted text-small">${category}</li>\n      `:`\n      <li\n        class="site-bookmark-li unit-0"\n        data-name="${addPinyin(name).toLowerCase()}"\n      >\n        <a target="_blank" href="${url}" class="site-bookmark-a flex-middle" tabindex="9">\n          ${renderFavicon(favicon)}\n          <span>${name}</span>\n        </a>\n      </li>\n    `}function renderFavicon(favicon){return favicon?`<img class="site-bookmark-img" src="https://cdn.staticaly.com/img/${favicon}" height="16" width="16" />`:""}function renderFooter(){return'\n      <footer class="text-center top-gap-big text-muted text-small">\n        <hr/>\n        <p>This navigation is built with <a class="text-muted" href="https://github.com/xcatliu/123">xcatliu/123</a>.</p>\n      </footer>\n    '}};